package com.example.recyclerviewpeliculas

import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import androidx.recyclerview.widget.GridLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.recyclerviewpeliculas.databinding.ActivityMainBinding

class MainActivity : AppCompatActivity() {
    private lateinit var binding: ActivityMainBinding
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(binding.root)
        val recyclerView = findViewById<RecyclerView>(R.id.recyclerViewPeliculas)
        val pelicula1 = Pelicula("Batman Returns", "https://pics.filmaffinity.com/Batman_vuelve-538628880-large.jpg");
        val pelicula2 = Pelicula("Avengers Endgame", "data:image/jpeg;base64,")
        val pelicula3 = Pelicula("Five nights at Freddy's", "data:image/jpeg;base64,")
        val pelicula4 = Pelicula("Avatar", "https://m.media-amazon.com/images/M/MV5BYjhiNjBlODctY2ZiOC00YjVlLWFlNzAtNTVhNzM1YjI1NzMxXkEyXkFqcGdeQXVyMjQxNTE1MDA@._V1_.jpg")

        val peliculasAdapter: ArrayList<Pelicula> = ArrayList<Pelicula>();
        peliculasAdapter.add(pelicula1);
        peliculasAdapter.add(pelicula2)
        peliculasAdapter.add(pelicula3)
        peliculasAdapter.add(pelicula4)
        recyclerView.adapter = PeliculasAdapter(peliculasAdapter);
        recyclerView.layoutManager = GridLayoutManager(this, 1);
    }
}